@implementation NSString (Validation)

- (BOOL)validEmailAddress {
	BOOL valid = NO;
	
	NSString *whiteSpaceStripped = [self stringByTrimmingCharactersInSet:[NSCharacterSet whitespaceAndNewlineCharacterSet]];
	
	NSPredicate *predicate = [NSPredicate predicateWithFormat:@"SELF MATCHES %@", emailRegexPattern];
	valid = [predicate evaluateWithObject:whiteSpaceStripped];
	
	return valid;
}

